import React, { useState } from 'react';

let numRows = 15;
let numCols = 15;

export const Grid = () => {
    const [grid, setGrid] = useState(() => {
        const rows = [];
        for (let i = 0; i < numRows; i++) {
            rows.push(Array.from(Array(numCols), () => 0 ));
        }
        return rows;
    })


    return (
        <div className='grid' style={{gridTemplateColumns: `${numCols}, 20px`}}>
            {grid.map ((rows, i) =>
                rows.map( (col, k) => <div classname='individual' key={`${i}-${k}`} style={{ backgroundColor: grid[i][k] ? 'green' : ''}} />
                ))}
        </div>
    )
}
