import React, { useState, useCallback, useRef, useContext } from 'react';
import {Button, Container, Col,  Row, Input} from 'reactstrap';
import produce from 'immer'; //to simplify state changing
import { GlobalContext } from '../Context/GlobalState';
import { helperFunctions } from '../Resources/functions';


//Initial values
let gridLayout = [50, 30]; //numRow, numCols
let speed = 1000;
const rows=[];


//positions from which the cell looks for neighbors
const neighborsLoc = [
    [0, 1], //north
    [1, 1], //north-east
    [1, 0], //east
    [1, -1], //south-east
    [0, -1], //south
    [-1, -1], //south-west
    [-1, 0], //west
    [-1, 1] //northwest
];


//helper functions

const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols]
    gridLayout = [...size];
    console.log(gridLayout);
};

const timeSet= (time) => {
    speed = time;
}

const countNeighbors = (grid, rows, cols) => {
    return neighborsLoc.reduce((acc, [xCoord, yCoord]) => { //this takes the neighborsLoc Arr and returns a number to see how many are alive
      const row = (rows + xCoord + gridLayout[0]) % gridLayout[0]; //checks every row and col to get alive (1) or dead(0) status
      const col = (cols + yCoord + gridLayout[1]) % gridLayout[1]; // we do this with the modulus operator
      acc += grid[row][col];
      return acc;
    }, 0);
  };


//component starts here  
export const Grid = () => {

    const {config} = useContext(GlobalContext);
    console.log(config.speed);


    //useState Hook variables
    //to create grid, we use useState hook
    const [grid, setGrid] = useState(() => {
        return clearGridX(gridLayout);
    });
    const [play, setPlay] = useState(false); //to set the play status
    const [autoTime, setAutoTime] = useState(500); //to set game speed
    const [generation, setGeneration] = useState(0); //to view current generation

    //useRef to store the value of play and using it on autoGame func. 
    const playRef = useRef(play);
    playRef.current = play; 

    //idem for generations
    const genRef = useRef(generation);
    genRef.current = generation;

    const nextGen = () => {
        setPlay(true);
        if(play === false){
            playRef.current = true;
            console.log('corriendo');
            autoGame();
        }
        setPlay(false);
        if(play) playRef.current = false;
    };

    //useCallback to run autoGame function only once
    const autoGame = useCallback(() => {
        setInterval(() => {

        //to ensure that the game stops, if the playRef is false we return the autoGame function
        if(playRef.current === false) {
            return;
        }

        setGrid(currentGrid => {  //changes the grid after looking out for neighbors.
            return produce(currentGrid, gridChanged => {
                for (let rowsIteration = 0; rowsIteration < gridLayout[0]; rowsIteration++) {
                    for (let colsIteration = 0; colsIteration < gridLayout[1]; colsIteration++) {
                        let livingNeighbors = countNeighbors(currentGrid, rowsIteration, colsIteration);                              
                        if (currentGrid[rowsIteration][colsIteration] === 1 && (livingNeighbors < 2 || livingNeighbors > 3)){
                        gridChanged[rowsIteration][colsIteration] = 0;
                        }
                        if (currentGrid[rowsIteration][colsIteration] === 0 && livingNeighbors === 3) {
                        gridChanged[rowsIteration][colsIteration] = 1;
                        }
                    }
                }
            });
        });
        setGeneration(++genRef.current); //increases generation by one per turn

        },speed); //guardar una variable de clear interval para que no suma
    },[]) //useCallback closes here, [] to run just once


    //return Statement starts here
    return (
        <Container className='justify-content-center'>
        <Row className='mb-4'>
            <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setPlay(true);
            if(play === false){
                playRef.current = true;
                autoGame();
            }
        }} >Iniciar</Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setPlay(false);
            if(play) playRef.current = false;
        }} >Detener </Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            nextGen();
        }} >Sig. Generacion </Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setGrid(clearGridX(gridLayout));
            setPlay(false);
            setGeneration(0);
        }} >Reiniciar </Button>
        </Col>
        <Col>
        <Input style={{width: '350px'}}
        type='number'
        placeholder='Introduce tiempo de ejecución deseado'
        onChange={e => {
            timeSet(e.target.value);
            setAutoTime(e.target.value);
        }}
        />
        </Col>
        </Row>
        <Row>
        <Col>
        <p>Juego {play ? 'Iniciado' : 'Detenido'} y con una velocidad de {autoTime/1000} seg </p>
        </Col>
        </Row>
        <Row>
        <Col>        
        <div className='d-flex justify-content-center'>
        <div style={{
            display: 'grid',
            gridTemplateColumns: `repeat(${gridLayout[1]}, 20px)`,
            columnGap: '5px',
            rowGap: '5px'
        }}>
            {/* this maps the grid's x and y coordinates */}
            {grid.map ((rows, xCoord) =>
                rows.map( (col, yCoord) => 
                <div 
                    key={`${xCoord}-${yCoord}`}
                    onClick={()=>{
                        //produce() takes the argument of individual grid, copies it and then mutates it.
                        const newGrid = produce(grid, gridEdited => {
                            //check if current cell at [xCoord][yCoord] is dead or alive, it toggles the value between them 
                            gridEdited[xCoord][yCoord] ^= 1;
                        });
                        setGrid(newGrid);
                }} 
                style={{ 
                    width:20, 
                    height:20, 
                    border: 'solid 1px blue',
                    borderRadius: '100%', 
                    backgroundColor: grid[xCoord][yCoord] ? 'green' : ''}} />
                ))}
                </div>
            </div>
            </Col>
            <Col>
            <p>Editar tamaños del grid</p>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none'}} onClick={()=>{
                gridSize(10,10);
                setGrid(clearGridX(gridLayout));
            }}> 10 x 10 </Button>
            </Col>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none'}} onClick={()=>{
                gridSize(20,30);
                setGrid(clearGridX(gridLayout));
            }}> 20 x 30 </Button>
            </Col>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none'}} onClick={()=>{
                gridSize(25,25);
                setGrid(clearGridX(gridLayout));
            }}> 25 x 25 </Button>
            </Col>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginBottom:'15px'}} onClick={()=>{
                gridSize(50,30);
                setGrid(clearGridX(gridLayout));
            }}> 50 x 30 </Button>
                        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginBottom:'15px'}} onClick={()=>{
                console.log(`${config.speed} aqui prueba`);
                setGrid(clearGridX(gridLayout));
            }}> Prueba de console log </Button>
            <Col>
            <p style={{fontSize:'14x'}}>Generación # {generation}</p>
            </Col>
            </Col>
        </Row>
        </Container>
    )
};
