import React, { useState } from 'react';

let numRows = 15;
let numCols = 15;

export const Grid = () => {
    const [grid, setGrid] = useState(() => {
        const rows = [];
        for (let i = 0; i < numRows; i++) {
            rows.push(Array.from(Array(numCols), () => 0 ));
        }
        return rows;
    })


    return (
        <div style={{

            display: 'grid',
            gridTemplateColumns: `repeat(${numCols}, 20px)`,
            columnGap: '5px',
            rowGap: '5px'
        }}>
            {grid.map ((rows, i) =>
                rows.map( (col, j) => <div 
                key={`${i}-${j}`} 
                style={{ width:20, height:20, border: 'solid 1px black',
                borderRadius: '100%', 
                backgroundColor: grid[i][j] ? 'green' : ''}} />
                ))}
        </div>
    )
}
