import React, {useState, useContext} from 'react';
import { GlobalContext } from '../Context/GlobalState';


export function Controls() {
    const { changeSpeed } = useContext(GlobalContext);
    const [speed, setSpeed] = useState(0);
    const changeLayout = (rows) => {
        gridSize(rows, 20);
    }
    return (
        <div>
            <input type='number' onChange={(e) => setSpeed(parseInt(e.target.value))} ></input>
            <button onClick={() => changeSpeed(speed)}>
            PRUEBA 
            </button>
            <p>hola {typeof speed[0]} {speed}</p>
        </div>
    )
}
