import React, {useState} from 'react'

let layOut = [];
const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols]
    console.log(layOut);
    console.log(size);
    return layOut = [...size];
};
export function Controls() {
    const [pruebaX, setPruebaX] = useState(0);
    const changeLayout = () => {
        gridSize(pruebaX, 20);
    }
    return (
        <div>
            <input type='number' onChange={(e) => setPruebaX(e.target.value)} ></input>
            <button onClick={() => changeLayout()}>
            PRUEBA 
            </button>
        </div>
    )
}

export const gridLayoutTest = layOut;
