import React, { useState } from 'react';

let numRows = 15;
let numCols = 15;

export const Grid = (height, width, random = false) => {
    const [grid, setGrid] = useState(() => {
        const rows = [];
        for (let i = 0; i < numRows; i++) {
            rows.push(Array.from(Array(numCols), () => 0 ));
        }
        return rows;
    });

    console.log(grid);
}
