import React, {useState, useContext} from 'react';
import { GlobalContext } from '../Context/GlobalState';


export function Controls() {
    const { addSpeed } = useContext(GlobalContext);
    const [speed, setSpeed] = useState(0);

    const onSubmit= e =>{
        e.preventDefault();

        const newSpeed = speed;
        addSpeed(newSpeed);
    }
    return (
        <div>
            <form onSubmit={onSubmit} ></form>
            <input type='number' onChange={(e) => setSpeed(parseInt(e.target.value))} ></input>
            <button onClick={() => addSpeed(speed)}>
            PRUEBA 
            </button>
        </div>
    )
}
