import React, {useState} from 'react'

let layOut = []
const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols, 'prueba']
    layOut = [...size];
    console.log(layOut);
    return size;
};
export function Controls() {
    const [pruebaX, setPruebaX] = useState(0);
    return (
        <div>
            <input type='number' onChange={(e) => setPruebaX(e.target.value)} ></input>
            <button onClick={() => gridSize(pruebaX,20)}>
            PRUEBA 
            </button>
        </div>
    )
}

export const gridLayoutTest = gridSize(pruebaX,20);
