import React, {createContext, useReducer} from "react";
import {ADD_SPEED, CHANGE_LAYOUT, CHANGE_SPEED } from './types';
import AppReducer from "./AppReducer";

// Estado inicial
const initialState = {
    config: {
        gridLayout: [50,30],
        speed: 1000,
    }
}
// Creaci贸n de contexto

export const GlobalContext = createContext(initialState);

// Componente provider
// Con provider le pasamos cualquier estado, acci贸n al os componentes wrapped, en este caso los componentes de app.js

export const GlobalProvider = ({children}) => {
    const [state, dispatch] = useReducer (AppReducer, initialState);

    //Actions
    function addSpeed(speed){
        dispatch({
            type: ADD_SPEED,
            payload: speed
        });
    }
    function changeSpeed(speed){
        dispatch({
            type: CHANGE_SPEED,
            payload: speed
        })
    }
    return (<GlobalContext.Provider value={{
        startConfig: state.config,
        addSpeed}}> 

        {children}
    </GlobalContext.Provider>)
    }
