import React, {useState} from 'react'

let layOut = [];
const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols]
    layOut = [...size];
    console.log(layOut);
    console.log(size);
};

export function Controls() {
    const [size, setSize] = useState(0);
    const changeLayout = () => {
        gridSize(size, 20);
    }
    return (
        <div>
            <input type='number' onChange={(e) => setSize(parseInt(e.target.value))} ></input>
            <button onClick={() => changeLayout()}>
            PRUEBA 
            </button>
            <p>hola {typeof size[0]} {size}</p>
        </div>
    )
}
