import {CHANGE_LAYOUT, CHANGE_SPEED } from './types';


// Un reducer es c贸mo especificamos los cambios de state de la aplicaci贸n en respuesta a algunas acciones.
export default (state, action) => {
    switch(action.type)  {
        case CHANGE_LAYOUT:
            return {
                ...state,
                layout: action.payload
            }
        case CHANGE_SPEED:
            return {
                ...state,
                transacciones: state.transacciones.filter(transaccion => transaccion._id !== action.payload)
            }
        default:
            return state;
    }
}
