import React, { useState } from 'react';

let numRows = 15;
let numCols = 15;

export const Grid = () => {
    const [grid, setGrid] = useState(() => {
        const rows = [];
        for (let i = 0; i < numRows; i++) {
            rows.push(Array.from(Array(numCols), () => 0 ));
        }
        return rows;
    })


    return (
        <div>
            {grid.map ((rows, i) =>
                rows.map( (col, k) => <div style={{ width:20, height:20, borderRadius: '1px!important', backgroundColor: grid[i][k] ? 'green' : '', border:'solid 1px black'}} />
                ))}
        </div>
    )
}
