import React, { useState, useCallback, useRef } from 'react';
import produce from 'immer';


let numRows = 10;
let numCols = 15;

const neighborsLoc = [
    [0, 1], //north
    [1, 1], //north-east
    [1, 0], //east
    [1, -1], //south-east
    [0, -1], //south
    [-1, -1], //south-west
    [-1, 0], //west
    [-1, 1] //northwest
];

const clearGrid = () => {
    const rows = [];
    //iterate from 0 to numRows (set by user) and for each row, create numCols (also set by user)
    for (let i = 0; i < numRows; i++) {
        //here we push to row Array using .from method, the length will be numCols and for each one
        //we map a value of 0 (dead cell state)
        rows.push(Array.from(Array(numCols), () => 0 ));
    }
    return rows;
};

const countNeighbors = (grid: any[][], x: number, y: number) => {
    return neighborsLoc.reduce((acc, [i, j]) => {
      const row = (x + i + numRows) % numRows;
      const col = (y + j + numCols) % numCols;
      acc += grid[row][col];
      return acc;
    }, 0);
  };

export const Grid = () => {
    //to create grid, we use useState hook
    const [grid, setGrid] = useState(() => {
        return clearGrid();
    });

    const [play, setPlay] = useState(false);
    const [autoTime, setAutoTime] = useState(1000);
    const [generation, setGeneration] = useState(0);

    //useRef to store the value of play and using it on autoGame func
    const playRef = useRef(play);
    playRef.current = play;

    //idem for generation
    const genRef = useRef(generation);
    genRef.current = generation;

    //useCallback to run autoGame function only once
    const autoGame = useCallback(() => {


        if(!playRef.current) {
            return;
        }
        setGrid(currentGrid => {  //changes the grid after looking out for neighbors
             produce(currentGrid, gridChanged => {
                for (let i = 0; i < numRows; i++) {
                    for (let j = 0; j < numCols; j++) {
                        const count = countNeighbors(currentGrid, i, j);
                        let livingNeighbors = 0;
                        neighborsLoc.forEach( ([x, y]) => { //looks around for living neighbors
                            const newI = i + x; //actual i location + the 
                            const newJ = j + y;
                            if (newI >= 0 && newI < numRows && newJ >= 0 && newJ < numCols){
                                livingNeighbors += currentGrid[newI][newJ]; //if alive, adds 1 if not then 0
                            }
                        });

                        if (livingNeighbors < 2 || livingNeighbors > 3) {
                            gridChanged[i][j] = 0; //if it has less than 2 living or more than 3, the cell dies
                        } else if (currentGrid[i][j] === 0 && livingNeighbors ===3){
                            gridChanged[i][j] = 1;
                        }

                    }
                }
            });
        });

        setTimeout(autoGame, autoTime);

    },[])

    return (
        <>
        <button onClick={()=>{
            setPlay(true);
            if(play === false){
                playRef.current = true;
                console.log('corriendo');
                autoGame();
            }

        }} >Iniciar</button>
        <button onClick={()=>{
            setPlay(false);
        }} >Detener </button>

        <button onClick={()=>{
            setGrid(clearGrid());
        }} >Reiniciar </button>

        <input
        placeholder='Introduce tiempo de ejecución deseado'
        onChange={e => setAutoTime(e.target.value)}
        />

        <p>Juego {play ? 'Iniciado' : 'Detenido'} y simulando a {autoTime}</p>

        <div style={{
            display: 'grid',
            gridTemplateColumns: `repeat(${numCols}, 20px)`,
            columnGap: '5px',
            rowGap: '5px'
        }}>
            {grid.map ((rows, i) =>
                rows.map( (col, j) => 
                <div 
                    key={`${i}-${j}`}
                    onClick={()=>{
                        //produce() takes the argument of individual grid, copies it and then mutates it.
                        //
                        const newGrid = produce(grid, gridCopy => {
                            //check if current cell at [i][j] is dead or alive, it toggles the value between them 
                            gridCopy[i][j] = grid[i][j] ? 0 : 1;
                        });
                    setGrid(newGrid);
                }} 
                style={{ 
                    width:20, 
                    height:20, 
                    border: 'solid 1px blue',
                    borderRadius: '100%', 
                    backgroundColor: grid[i][j] ? 'green' : ''}} />
                ))}
        </div>
        </>
    )
};
