import React, { useState, useCallback, useRef } from 'react';
import {Button, Container, Col,  Row, Input} from 'reactstrap';
import {  } from '../Resources/functions';
import produce from 'immer';

//Initial values
let gridLayout = [50, 30];
let speed = 1000;
// let numRows = 50;
// let numCols = 30;

//positions from which the cell looks for neighbors
const neighborsLoc = [
    [0, 1], //north
    [1, 1], //north-east
    [1, 0], //east
    [1, -1], //south-east
    [0, -1], //south
    [-1, -1], //south-west
    [-1, 0], //west
    [-1, 1] //northwest
];

const clearGrid = () => {
    const rows = [];
    //iterate from 0 to numRows (set by user) and for each row, create numCols (also set by user)
    for (let i = 0; i < gridLayout[0]; i++) {
        //here we push to row Array using .from method, the length will be numCols and for each one
        //we map a value of 0 (dead cell state)
        rows.push(Array.from(Array(gridLayout[1]), () => 0 ));
    }
    return rows;
};

const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols]
    gridLayout = [...size];
    console.log(gridLayout);
};
const timeSet= (time) => {
    speed = time;
}

const countNeighbors = (grid, x, y) => {
    return neighborsLoc.reduce((acc, [i, j]) => {
      const row = (x + i + gridLayout[0]) % gridLayout[0];
      const col = (y + j + gridLayout[1]) % gridLayout[1];
      acc += grid[row][col];
      return acc;
    }, 0);
  };

export const Grid = () => {
    //to create grid, we use useState hook
    const [grid, setGrid] = useState(() => {
        return clearGrid();
    });

    const [play, setPlay] = useState(false);
    const [autoTime, setAutoTime] = useState(500);
    const [generation, setGeneration] = useState(0);

    const nextGen = () => {
        setPlay(true);
        if(play === false){
            playRef.current = true;
            console.log('corriendo');
            autoGame();
        }
        setPlay(false);
        if(play) playRef.current = false;
    };



    //useRef to store the value of play and using it on autoGame func
    const playRef = useRef(play);
    playRef.current = play;

    //idem for generations
    const genRef = useRef(generation);
    genRef.current = generation;

    //useCallback to run autoGame function only once
    const autoGame = useCallback(() => {
        //to ensure that the game stops
        if(!playRef.current) {
            return;
        }

        setGrid(currentGrid => {  //changes the grid after looking out for neighbors
            return produce(currentGrid, gridChanged => {
                for (let i = 0; i < gridLayout[0]; i++) {
                    for (let j = 0; j < gridLayout[1]; j++) {
                        let livingNeighbors = countNeighbors(currentGrid, i, j);                              
                        if (currentGrid[i][j] === 1 && (livingNeighbors < 2 || livingNeighbors > 3))
                        gridChanged[i][j] = 0;
                        if (!currentGrid[i][j] && livingNeighbors === 3) gridChanged[i][j] = 1;
                    }
                }
            });
        });
        setGeneration(++genRef.current); //increases generation by one per turn

    setTimeout(autoGame, speed);

    },[]) //useCallback closes here

    return (
        <Container className='justify-content-center'>
        <Row className='mb-4'>
            <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setPlay(true);
            if(play === false){
                playRef.current = true;
                autoGame();
            }
        }} >Iniciar</Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setPlay(false);
            if(play) playRef.current = false;
        }} >Detener </Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            nextGen();
        }} >Sig. Generacion </Button>
        </Col>
        <Col>
        <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
            setGrid(clearGrid());
            setPlay(false);
            setGeneration(0);
        }} >Reiniciar </Button>
        </Col>
        <Col>
        <Input style={{width: '300px'}}
        type='number'
        placeholder='Introduce tiempo de ejecución deseado'
        onChange={e => {
            timeSet(e.target.value);
            setAutoTime(e.target.value);
        }}
        />
        </Col>
        <Col>
            <p style={{fontSize:'16px'}}>Generación # {generation}</p>
        </Col>
        </Row>
        <Row>
        <Col>
        <p>Juego {play ? 'Iniciado' : 'Detenido'} y con una velocidad de {autoTime/1000} seg</p>
        </Col>
        </Row>
        <Row>
        <Col>        
        <div className='d-flex justify-content-center'>
        <div style={{
            display: 'grid',
            gridTemplateColumns: `repeat(${gridLayout[1]}, 20px)`,
            columnGap: '5px',
            rowGap: '5px'
        }}>
            {grid.map ((rows, i) =>
                rows.map( (col, j) => 
                <div 
                    key={`${i}-${j}`}
                    onClick={()=>{
                        //produce() takes the argument of individual grid, copies it and then mutates it.
                        //
                        const newGrid = produce(grid, gridCopy => {
                            //check if current cell at [i][j] is dead or alive, it toggles the value between them 
                            gridCopy[i][j] ^= 1;
                        });
                        setGrid(newGrid);
                }} 
                style={{ 
                    width:20, 
                    height:20, 
                    border: 'solid 1px blue',
                    borderRadius: '100%', 
                    backgroundColor: grid[i][j] ? 'green' : ''}} />
                ))}
                </div>
            </div>
            </Col>
            <Col>
            <p>Editar tamaños del grid</p>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
                gridSize(10,10);
                setGrid(clearGrid());
            }}> 10 x 10 </Button>
            </Col>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
                gridSize(20,30);
                setGrid(clearGrid());
            }}> 20 x 30 </Button>
            </Col>
            <Col className='mb-3'>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
                gridSize(25,25);
                setGrid(clearGrid());
            }}> 25 x 25 </Button>
            </Col>
            <Col>
            <Button style={{backgroundColor:'#4dd2ff', color:'white', border:'none', marginRight:'20px'}} onClick={()=>{
                gridSize(50,30);
                setGrid(clearGrid());
            }}> 50 x 30 </Button>
            </Col>
            </Col>
        </Row>
        </Container>
    )
};
