import React, {useState, useContext} from 'react';
import { GlobalContext } from '../Context/GlobalState';


let layOut = [];
const gridSize = (rows = 50, cols = 30) => {
    let size = [rows, cols]
    layOut = [...size];
    console.log(layOut);
    console.log(size);
};

export function Controls() {

    const { changeSpeed } = useContext(GlobalContext);
    const [sizeX, setSizeX] = useState(0);
    const changeLayout = (rows) => {
        gridSize(rows, 20);
    }
    return (
        <div>
            <input type='number' onChange={(e) => setSizeX(parseInt(e.target.value))} ></input>
            <button onClick={() => changeSpeed()}>
            PRUEBA 
            </button>
            <p>hola {typeof sizeX[0]} {sizeX}</p>
        </div>
    )
}
