import {CHANGE_LAYOUT, ADD_SPEED, CHANGE_SPEED } from './types';


// Un reducer es c贸mo especificamos los cambios de state de la aplicaci贸n en respuesta a algunas acciones.
export default (state, action) => {
    switch(action.type)  {
        case CHANGE_LAYOUT:
            return {
                ...state, //create new state
                layout: action.payload
            }
        case ADD_SPEED:
            return {
                ...state,
                speed: action.payload
            }
        case CHANGE_SPEED:
            return {
                ...state,
                speed: state.config = action.payload
            }
        default:
            return state;
    }
}
